import React, { useState, useEffect, useCallback } from "react";
import { Container, ProgressBar, Col, Row } from "react-bootstrap";
import CheckCorrect from "../../components/checkCorrect/checkCorrect";
import Lesson from "../../pages/lesson/lesson";
import {
    BrowserRouter as Router, Link, useHistory
} from 'react-router-dom';
import { useParams } from 'react-router-dom';
import cross from './../../assets/images/cross.png';
import { useSelector, useDispatch } from 'react-redux';
import { loadQuestions, selectQuestions } from '../../features/questions/questions.slice';
import './lessonLayout.css';

export default function LessonLayout(props) {

    const [questionQueue, setQuestionQueue] = useState(0);
    const [questions, setQuestions] = useState([])
    let q = []
    q = useSelector(selectQuestions)
    setQuestions(q)
    let dispatch = useDispatch()
    const { step } = useParams();

    useEffect(() => {
        dispatch(loadQuestions(step));
        let initialQuestionQueue = [];
        if (questions.length && questions.length !== 0) {
            for (let i = 0; i < questions.length; i++) {
                initialQuestionQueue[i] = i
            }
            setQuestionQueue(initialQuestionQueue)
        }

    }, [])



    return (

        < div >

            {

                q.map((q, key) => {
                    return (
                        <div>
                            {q.options}
                        </div>
                    )
                })
            }
        </div >

    )
}

